    		SELECT Board.* FROM Board, conn_bf, conn_bl



	    			WHERE (conn_bf.bid=Board.bid AND conn_bf.fid IN 

						#{fid}

						   )
					OR (conn_bl.bid=Board.bid AND conn_bl.lid IN

